import React, { useState } from "react";
import Stack from "@mui/material/Stack";
import Button from "@mui/material/Button";
import InputComponent from "../components/InputComponent";
import SelectionComponent from "../components/SelectionComponent";
import "../components/Gallery.css";
import HeroSection from "../components/HeroSection";

function Rsvp() {
  const [name, setName] = useState();
  const [reservation, setReservation] = useState(0);
  const [attend, setAttend] = useState();
  const nameUpdate = (event) => {
    // Dealing with name field changes to update our state
    setName(event.target.value);
  };
  const reservationUpdate = (event) => {
    // Dealing with reservation field changes to update our state
    setReservation(event.target.value);
  };
  const attendUpdate = (event) => {
    // Dealing with reservation field changes to update our state
    setAttend(event.target.value);
  };
  const attendOptions = ["Yes", "No"];
  const reservationOptions = ["1", "2", "3"];
  const onSubmit = () => {
    console.log(`${name} submmited ${reservation}`);
  };
  return (
    <div className="cards">
      <HeroSection
      title={"Celebrate with us"}
      description={"Let us know if you're coming by Nov 15, 2022!"}
    />
      
      <div className="cards__container">
        <Stack spacing={1} direction="column">
          <Stack spacing={1} direction="row">
            <InputComponent
              label="First Name"
              inputType="text"
              onChange={nameUpdate}
              variant="filled"
            />
            <InputComponent
              label="Last Name"
              inputType="text"
              onChange={nameUpdate}
              variant="filled"
            />
          </Stack>
          <SelectionComponent
            label="Are you attending?"
            onChange={attendUpdate}
            options={attendOptions}
            variant="filled"
          />
          <SelectionComponent
            label="Reservations"
            onChange={reservationUpdate}
            options={reservationOptions}
            variant="filled"
            helperText="Total guests attending (including yourself)"
          />
          <Button variant="contained" onClick={onSubmit}>
            Reserve
          </Button>
        </Stack>
      </div>
    </div>
  );
}

export default Rsvp;
