import React from "react";
import TextField from '@mui/material/TextField'
import MenuItem from '@mui/material/MenuItem';

function SelectionComponent({ label, onChange, variant, options, helperText }) {
  return (
		<div>
		<TextField
			required
			select
			id={label}
			label={label}
			variant={variant}
			onChange={onChange}
			helperText={helperText}
		>
			{options.map((option) => (
				<MenuItem key={option} value={option}>
					{option}
				</MenuItem>
			))}
		</TextField>					
		</div>		
  );
}

export default SelectionComponent;
